mapper.insert:
insert into member (


 		phone


 		postcode, address1, address2


 )values(


 		#{phone}


 		#{postcode},#{address1},#{address2}


 )

mapper.update:
update member set


		name=#{name}


		phone=#{phone}


		postcode=#{postcode},address1=#{address1},address2=#{address2} 


where email=#{email}
